void drawAlarmRing(int a) {
    tft.fillScreen(0x0);
    // dolphin_happy
    tft.drawBitmap(131, 82, image_dolphin_happy_bits, 59, 63, 0xFFFF);
    // alarm_text2
    tft.setTextColor(0xFFFF);
    tft.setTextSize(1);
    tft.setFreeFont(&FreeSerif24pt7b);
    char rang[10];
    sprintf(rang, "Alarm %d", a);
    tft.drawString(rang, 83, 33);
    // alarm_text2
    tft.drawString("is up!", 111, 145);
    // confirm_btn
    tft.setTextColor(0x651D);
    tft.setFreeFont(&Petme8x8);
    tft.drawString("Confirm", 132, 217);
}

